import React,{ useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { storage } from "../constants/firebase";

function MovieDetailCard({ movie }) {

  let pathReference = storage.refFromURL("gs://movie-app-d4c77.appspot.com/poster/");
  let starsRef = pathReference.child("cover2.jpg");

  starsRef
    .getDownloadURL()
    .then((url) => {
      let img = document.querySelector(".avatar");
      img.src = url;
    })
    .catch((error) => {
      console.log(error);
      switch (error.code) {
        case "storage/object-not-found":
          break;

        case "storage/unauthorized":
          // User doesn't have permission to access the object
          break;

        case "storage/canceled":
          // User canceled the upload
          break;

        case "storage/unknown":
          // Unknown error occurred, inspect the server response
          break;

        default:
          break;
      }
    });
    useEffect(() => {
      fetchData();
    }, []);
  return (
    <div className="card card--list">
      <div className="row">
        <div className="col-12 col-sm-4">
          <div className="card__cover">
            <img src={movie.poster} alt="" />
            <Link to={{
              pathname: `movie/${movie.id}/dadas`,
              search: "?search=25"
            }}
              className="card__play">
              <i className="icon ion-ios-play"></i>
            </Link>
          </div>
        </div>

        <div className="col-12 col-sm-8">
          <div className="card__content">
            <h3 className="card__title">
              <Link to={`movie/${movie.id}`}>{movie.title}</Link>
            </h3>
            <span className="card__category">
              {movie.genres.map((genre) => {
                return (
                  // <a href="#" key={genre.id}>
                  //   {genre.name}
                  // </a>
                  <Link
                    to={{
                      pathname: `genre/${genre.name}/1`,
                      // search: "?sort=name",
                      state: { genre: genre }
                    }}
                  >{genre.name}</Link>
                );
              })}
            </span>

            <div className="card__wrap">
              <span className="card__rate">
                <i className="icon ion-ios-star"></i>
                {movie.imdb}
              </span>

              <ul className="card__list">
                <li>{movie.resolution}</li>
                {movie.limitAge === "NoLimit" ? null : (
                  <li>{movie.limitAge}</li>
                )}
              </ul>
            </div>

            <div className="card__description">
              <p>{movie.desc}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MovieDetailCard;
